package com.syscho.book.dao;

import java.util.ArrayList;
import java.util.List;
import java.util.UUID;

import com.syscho.book.vo.Book;

public class BookDAO {

	static List<Book> bookList = new ArrayList<>();

	private final String DEFAULT_IMG = "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTZOUM-_ZVIXP0PF5VuQNOZE_kF4fp_RYjkriUMhfGbv10RvfIq";

	static {

		bookList.add(new Book(UUID.randomUUID(), "Bossypants", "Comedy", 150,
				"https://pixel.nymag.com/imgs/daily/vulture/2018/05/splitsider/comedy-books/bossypants-tina-fey.w245.h367.jpg",
				"Tina Fey"));
		bookList.add(new Book(UUID.randomUUID(), "Forever", "Comedy", 750,
				"data:image/jpeg;base64,",
				"Erma Bombeck"));
		bookList.add(new Book(UUID.randomUUID(), "Mr. Mike", "Comedy", 650,
				"https://pixel.nymag.com/imgs/daily/vulture/2018/05/splitsider/comedy-books/mr-mike-dennis-perrin.w245.h367.jpg",
				"Dennis Perrin"));
		bookList.add(new Book(UUID.randomUUID(), "New", "Drama", 750,
				"https://pixel.nymag.com/imgs/daily/vulture/2018/05/splitsider/comedy-books/forever-erma-erma-bombeck.w245.h367.jpg",
				"Erma Bombeck"));

	}

	public List<Book> getBooks() {
		return bookList;

	}

	public boolean addBook(Book book) {
		if (null == book.getBookImgUrl() ||book.getBookImgUrl().isEmpty()) {
			book.setBookImgUrl(DEFAULT_IMG);
		}
		return bookList.add(book);
	}

}
